mapper.insert:

	select coalesce(max(blog_priority),0) from tbl_blog_setting
	where blog_category = #{blog_category} 
	and user_id = #{user_id}

insert into tbl_blog_setting 
	(user_id, blog_category, config_name, config_value, blog_priority )
values (#{user_id}, #{blog_category}, #{config_name}, #{config_value}, #{blog_priority} + 1 )

mapper.update:
update tbl_blog_setting
set isMain = #{isMain}, isVisible = #{isVisible}, blog_category = #{blog_category}, config_value = #{config_value}, blog_priority = #{blog_priority}
where user_id = #{user_id} and config_name = #{config_name}
