mapper.update:
update tbl_notice set
title = #{title},
content = #{content}
where n_no = #{n_no}

mapper.select:
		 select * 
    from (
        select * 
        from tbl_notice 
        where n_no < #{n_no} 
        order by n_no desc
    ) 
    where rownum = 1

	  select * 
        from (
            select * 
            from tbl_notice 
            where n_no > #{n_no} 
            order by n_no asc
        ) 
        where rownum = 1

mapper.update:
update tbl_notice
set important = #{important}
where n_no = #{n_no}
