mapper.update:
update tbl_users
set grade = #{grade}
where user_id = #{user_id}

mapper.select:
select user_id,user_name,grade,email,last_login 
from tbl_users
where user_id = #{user_id}

select *
from tbl_users
where user_id = #{user_id}
and user_pw = #{user_pw}

mapper.update:
update tbl_users set 
	user_pw = #{user_pw}
where user_id = #{user_id}
